    return (
      <div>
        <header>
          <Head></Head>
        </header>
        <div>
          {this.state.topics[0]}
          <Body></Body>
        </div>
        <footer>
          <Foot name={this.state.username}></Foot>
        </footer>
      </div>
    )
  }
}

export default App
