import React from 'react'
import Head from './components/Head'
import Body from './components/Body'
import Foot from './components/Foot'
class App extends React.Component {
  /**
   * props    :    사용자가 컴포넌트르 이용하는데 있어서 중요한 것
   * state    :    내부의 구현에 필요한 데이터들 ( 사용자가 알 필요없고 알아서도 안되는 부분 )
   */
  constructor(props) {
    super(props)
    this.state = {
      username: 'seongbeom',
      topics: [],
    }
  }
  /**
         "id": 2,
      "title": "Oracle",
      "description": "Oracle is ...",
      "created": "2018-01-03T04:01:10.000Z",
      "author_id": 1
   */
  componentDidMount() {
    fetch('http://localhost:3001/topic')
      .then(res => res.json())
      .then(res => {
        for (var i = 0; i < res.topics.length; i++) {
          const topic = res.topics[i]
          var _topics = Array.from(this.state.topics)
          _topics.push({
            id: topic.id,
            title: topic.title,
            description: topic.description,
            created: topic.created,
            author_id: topic.author_id,
          })
        }
        this.setState({ topics: _topics })
      })
  }

  getContent() {
    var _title,
      desc = null
  }
  render() {
    return (
      <div>
        <header>
          <Head></Head>
        </header>
        <div>
          {this.state.topics[0]}
          <Body></Body>
        </div>
        <footer>
          <Foot name={this.state.username}></Foot>
        </footer>
      </div>
    )
  }
}

export default App
