import React from 'react'
import Head from './components/Head'
import Body from './components/Body'
import Foot from './components/Foot'
class App extends React.Component {
  /**
   * props    :    사용자가 컴포넌트르 이용하는데 있어서 중요한 것
   * state    :    내부의 구현에 필요한 데이터들 ( 사용자가 알 필요없고 알아서도 안되는 부분 )
   */
  constructor(props) {
    super(props)
    this.state = {
      username: 'seongbeom',
      topics: null,
    }

    // this.Gettopics = function (res) {
    //   let _topics = Array.from(this.state.topics)
    //   var i
    //   for (i = 0; i < res.topics.length; i++) {
    //     _topics.push({
    //       id: res.topics[i].id,
    //       title: res.topics[i].title,
    //       description: res.topics[i].description,
    //       created: res.topics[i].created,
    //       author_id: res.topics[i].author_id,
    //     })
    //   }
    //   this.setState({ topics: _topics })
    // }
  }
  componentDidMount() {
    fetch('http://localhost:3001/topic')
      .then(res => res.json())
      .then(res => this.setState({ topics: res.topics }))
  }

  render() {
    if (this.state.topics != null) {
      console.log(this.state.topics)
      return (
        <div>
          <header>
            <Head data={this.state.topics}></Head>
          </header>
          <div>
            <Body></Body>
          </div>
          <footer>
            <Foot name={this.state.username}></Foot>
          </footer>
        </div>
      )
    } else {
      return (
        <div>
          <header>
            <Head data={this.state.topics}></Head>
          </header>
          <div>
            <Body></Body>
          </div>
          <footer>
            <Foot name={this.state.username}></Foot>
          </footer>
        </div>
      )
    }
  }
}

export default App
