import React, { Component } from 'react'
import './Body.css'

class Body extends Component {
  constructor(props) {
    super(props)
    this.state = {
      topics: null,
    }
  }

  componentDidMount() {
    fetch('http://localhost:3001/topic')
      .then(res => res.json())
      .then(res => this.setState({ topics: res.topics }))
  }
  render() {
    return (
      <div className="body-container">
        <div className="body-left">글 목록</div>
        <div className="body-right">
          <div className="body-title">{this.props.topics[0].title}</div>
          <div className="body-description">
            {this.props.topics[0].description}
          </div>
        </div>
      </div>
    )
  }
}

export default Body
