import './Body.css'

class Body extends Component {
  constructor(props) {
    super(props)
    this.state = {
      isLoading: false,
      topics: null,
    }
  }

  componentDidMount() {
    fetch('http://localhost:3001/topic')
      .then(res => res.json())
      .then(res =>
        this.setState({
          topics: res.topics,
          isLoading: true,
        })
      )
  }

